import { Component } from 'react';


class MovieCard extends Component {
    render() {
        const list = this.props.genres.map(e => {
            return <div genre = {e} />
        })
        return (
            <div className="movie-card" >
                <h1>{this.props.title}</h1>
                <img src={this.props.posterSrc} />
                <h3>{list}</h3>

            </div>
        )
    }

}

export default MovieCard;
