import randomColor from "randomcolor";
import React, {useState, useEffect} from "react";
function App() {
  const [count, setCount] = useState(0);

  function increment() {
    setCount(count +1);
  }
  function decrement() {
    setCount(prevCount => prevCount -1);
  }
  return (
    <div className="container">
      <button className="row" onClick={increment}>+</button>
      <h1 className="row"> {count} </h1>
      <button className="row" onClick={decrement}>-</button>
    </div>
  );
}

export default App;
