import React from "react";
import { useState } from "react";
import './App.css';

function App() {
  const [count, setCount] = useState(4);

  const decrement = () => {
    setCount(preCount => preCount -1);
  }

  return (
    <div className="App">
      <button>-</button>
      <h1>{decrement(count)}</h1>
      <button>+</button>
    </div>
  );
}

export default App;
