import React from "react";
import { useState } from "react";
import './App.css';

function App() {
  // const [count, setCount] = useState(() => 4);
  const [state, setState] = useState( {count: 4, theme: "blue"} );

  const decrement = () => {
    setCount(prevState => {
      return {...prevState}
    });
  }
  const increment = () => {
    setCount(preCount => preCount + 1);
  }

  return (
    <div className="App">
      <button onClick={count > -1? decrement: setCount(0)}>-</button>
      <span >{count}</span>
      <button onClick={increment}>+</button>
    </div>
  );
}

export default App;
