import React from "react";
import { useState } from "react";
import './App.css';

function App() {
  const [count, setCount] = useState(4);

  const decrement = () => {
    setCount(preCount => preCount -1);
  }
  const increment = () => {
    setCount(preCount => preCount + 1);
  }

  return (
    <div className="App">
      <button onClick={count > -1? decrement: setCount(0)}>-</button>
      <span >{count}</span>
      <button onClick={increment}>+</button>
    </div>
  );
}

export default App;
